import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Textarea } from "@/components/ui/textarea";
import { Plus, Edit, Trash2, Calendar } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { Convention } from "@shared/schema";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { format } from "date-fns";
import { ar } from "date-fns/locale";

interface AdministrativeEvent {
  id: number;
  conventionId: number;
  eventDate: string;
  eventDescription: string;
  notes: string | null;
  createdAt: Date;
  updatedAt: Date;
}

interface AdministrativeTrackingProps {
  convention: Convention;
}

export function AdministrativeTracking({ convention }: AdministrativeTrackingProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingEvent, setEditingEvent] = useState<AdministrativeEvent | null>(null);
  const [formData, setFormData] = useState({
    eventDate: "",
    eventDescription: "",
    notes: "",
  });

  const { data: events = [], isLoading } = useQuery<AdministrativeEvent[]>({
    queryKey: [`/api/conventions/${convention.id}/administrative-events`],
  });

  const createMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      const response = await fetch(`/api/conventions/${convention.id}/administrative-events`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("خطأ في إنشاء الحدث الإداري");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/conventions/${convention.id}/administrative-events`] });
      setIsDialogOpen(false);
      resetForm();
      toast({
        title: "نجح",
        description: "تم إضافة الحدث الإداري بنجاح",
      });
    },
    onError: () => {
      toast({
        title: "خطأ",
        description: "حدث خطأ أثناء إضافة الحدث الإداري",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: typeof formData }) => {
      const response = await fetch(`/api/administrative-events/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("خطأ في تحديث الحدث الإداري");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/conventions/${convention.id}/administrative-events`] });
      setIsDialogOpen(false);
      resetForm();
      toast({
        title: "نجح",
        description: "تم تحديث الحدث الإداري بنجاح",
      });
    },
    onError: () => {
      toast({
        title: "خطأ",
        description: "حدث خطأ أثناء تحديث الحدث الإداري",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await fetch(`/api/administrative-events/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      if (!response.ok) throw new Error("خطأ في حذف الحدث الإداري");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/conventions/${convention.id}/administrative-events`] });
      toast({
        title: "نجح",
        description: "تم حذف الحدث الإداري بنجاح",
      });
    },
    onError: () => {
      toast({
        title: "خطأ",
        description: "حدث خطأ أثناء حذف الحدث الإداري",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      eventDate: "",
      eventDescription: "",
      notes: "",
    });
    setEditingEvent(null);
  };

  const handleEdit = (event: AdministrativeEvent) => {
    setEditingEvent(event);
    setFormData({
      eventDate: event.eventDate,
      eventDescription: event.eventDescription,
      notes: event.notes || "",
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.eventDate || !formData.eventDescription) {
      toast({
        title: "خطأ",
        description: "يرجى ملء جميع الحقول المطلوبة",
        variant: "destructive",
      });
      return;
    }

    if (editingEvent) {
      updateMutation.mutate({ id: editingEvent.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleDelete = (id: number) => {
    if (window.confirm("هل أنت متأكد من حذف هذا الحدث الإداري؟")) {
      deleteMutation.mutate(id);
    }
  };

  const formatDate = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return format(date, "dd/MM/yyyy", { locale: ar });
    } catch {
      return dateString;
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            التتبع الإداري
          </CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-center text-muted-foreground">جاري التحميل...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            التتبع الإداري
          </CardTitle>
          <Button
            onClick={() => {
              resetForm();
              setIsDialogOpen(true);
            }}
            size="sm"
            data-testid="button-add-administrative-event"
          >
            <Plus className="h-4 w-4 ml-2" />
            إضافة حدث
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        {events.length === 0 ? (
          <p className="text-center text-muted-foreground py-8">
            لا توجد أحداث إدارية مسجلة
          </p>
        ) : (
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead className="text-right">التاريخ</TableHead>
                  <TableHead className="text-right">الحدث</TableHead>
                  <TableHead className="text-right">ملاحظات</TableHead>
                  <TableHead className="text-right">الإجراءات</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {events.map((event) => (
                  <TableRow key={event.id} data-testid={`row-event-${event.id}`}>
                    <TableCell className="text-right" data-testid={`text-event-date-${event.id}`}>
                      {formatDate(event.eventDate)}
                    </TableCell>
                    <TableCell className="text-right" data-testid={`text-event-description-${event.id}`}>
                      {event.eventDescription}
                    </TableCell>
                    <TableCell className="text-right" data-testid={`text-event-notes-${event.id}`}>
                      {event.notes || "-"}
                    </TableCell>
                    <TableCell className="text-right">
                      <div className="flex gap-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleEdit(event)}
                          data-testid={`button-edit-event-${event.id}`}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleDelete(event.id)}
                          data-testid={`button-delete-event-${event.id}`}
                        >
                          <Trash2 className="h-4 w-4 text-destructive" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        )}
      </CardContent>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>
              {editingEvent ? "تعديل الحدث الإداري" : "إضافة حدث إداري جديد"}
            </DialogTitle>
            <DialogDescription>
              {editingEvent
                ? "قم بتعديل معلومات الحدث الإداري"
                : "أضف حدثاً إدارياً جديداً للإتفاقية"}
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="eventDate">
                تاريخ الحدث <span className="text-destructive">*</span>
              </Label>
              <Input
                id="eventDate"
                type="date"
                value={formData.eventDate}
                onChange={(e) =>
                  setFormData({ ...formData, eventDate: e.target.value })
                }
                required
                data-testid="input-event-date"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="eventDescription">
                وصف الحدث <span className="text-destructive">*</span>
              </Label>
              <Input
                id="eventDescription"
                value={formData.eventDescription}
                onChange={(e) =>
                  setFormData({ ...formData, eventDescription: e.target.value })
                }
                placeholder="مثال: إرسال الإتفاقية للتأشيرة"
                required
                data-testid="input-event-description"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="notes">ملاحظات</Label>
              <Textarea
                id="notes"
                value={formData.notes}
                onChange={(e) =>
                  setFormData({ ...formData, notes: e.target.value })
                }
                placeholder="أضف ملاحظات إضافية..."
                rows={3}
                data-testid="input-event-notes"
              />
            </div>

            <div className="flex gap-2 justify-end">
              <Button
                type="button"
                variant="outline"
                onClick={() => {
                  setIsDialogOpen(false);
                  resetForm();
                }}
                data-testid="button-cancel"
              >
                إلغاء
              </Button>
              <Button
                type="submit"
                disabled={createMutation.isPending || updateMutation.isPending}
                data-testid="button-submit"
              >
                {editingEvent ? "تحديث" : "إضافة"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </Card>
  );
}
